import React, { Component } from 'react';

class BotSearch extends Component {
    constructor(){
        super()
        this.state = {
            query: ''
        }
    }

    handleChange = (event) =>{
        this.setState({
            [event.target.name]: event.target.value
        })
        this.props.handleChange(event.target.value)
    }

    handleSearch = (event) => {
        event.preventDefault()
        this.props.handleClear(this.state.query)
        this.setState({
            query: ''
        })
    }

    render() {
        return (
            <div class="container">
            <nav class="navbar navbar-dark bg-light">
                 <div class="container-fluid">
                      <form class="d-flex">
                            <input class="form-control me-4" placeholder="Search" aria-label="Search" value={this.state.query} type="text" onChange={event => this.handleChange(event)}></input>
                                <button class="btn btn-outline-success"  onClick={event => this.handleSearch(event)} type="submit">Search</button>
                      </form>
                 </div>
            </nav>
        </div>
        );
    }
}

export default BotSearch;
